        while ((len = is.read(b, 0, 1024)) != -1) {
            os.write(b, 0, len);
        }
        return os;
    } catch (IOException ignored) {
        return null;
    } finally {
        try {
            is.close();
        } catch (IOException ignored) {
        }
    }
