{
        String imei = null;
        if (TextUtils.isEmpty(imei)) {
            // android L
            try {
                final String MZ_T_M = "android.telephony.MzTelephonyManager";
                final String METHOD_GET_DEVICE_ID = "getDeviceId";
                imei = (String) ReflectHelper.invokeStatic(MZ_T_M, METHOD_GET_DEVICE_ID, null, null);
            } catch (Exception ignore) {
            }

            if (TextUtils.isEmpty(imei)) {
                try {
                    final String MZ_T_M = "com.meizu.telephony.MzTelephonymanager";
                    final String METHOD_GET_DEVICE_ID = "getDeviceId";
                    imei = (String) ReflectHelper.invokeStatic(MZ_T_M, METHOD_GET_DEVICE_ID, new Class<?>[]{Context.class, int.class}, new Object[]{context, 0});
                } catch (Exception ignore) {
                }
            }

            // 这个处理是非必须的，因为METHOD_GET_DEVICE_ID本身做了这个处理；为了运行在其它手机平台，这里兼容处理
            if (TextUtils.isEmpty(imei)) {
                TelephonyManager tm = (TelephonyManager) context.getSystemService(Context.TELEPHONY_SERVICE);
                imei = tm.getDeviceId();
            }
        }
        return imei;
    }

